library(shiny)
library(bslib)
library(bsicons)
library(shinyjs)


# https://www.mdcalc.com/calc/10528/neonatal-early-onset-sepsis-calculator#when-to-use
# https://neonatalsepsiscalculator.kaiserpermanente.org/

server <- function(input, output, session) {
  
  showModal(modalDialog(
    title = "NO ANTIBIOTIC TREATMENT IS NOT THE SAME AS NO CARE!",
    footer = modalButton("Ok"),
    size = "m",
    fade = TRUE,
    "Close clinical observation and increased frequency of infant vital sign 
    ascertainment is required to ensure success in decreasing empiric antibiotic use 
    WITHOUT adverse effects. A combination of using the EOS risk calculator ALONG 
    WITH clinical vigilance offers the best approach, where antibiotic overuse is 
    avoided, and infants who clinically deteriorate are detected and treated early."
    )
  )
  
  # Logistic regression coefficients, β
  betaTemp = 0.868
  betaGA = -6.9325
  betaGA2 = 0.0877
  betaROM =  1.2256
  betaGbsIapORanyABnotOnTime = -1.0488
  betaBroadSpectrumAbOnTime = -1.1861
  betaGbsPos = 0.5771
  betaGbsUnknown = 0.0427
  
  # UPDATE to the Neonatal Early-Onset Sepsis Calculator Utilizing a Contemporary Cohort
  # Intercept: 57.29929499
  
  # betaTemp = 0.85194656
  # betaGA = -7.72247124
  # betaGA2 = 0.09842383
  # betaROM =  0.86770862
  # betaGbsIapORanyABnotOnTime = -2.13142945
  # betaBroadSpectrumAbOnTime = -2.33985917
  # betaGbsPos = 1.02265353
  # betaGbsUnknown = 1.13710111
  
  # Likelihood ratio's
  lrWell = 0.36             # well appearing, 0.36 (0.31–0.41).
  lrWellLL = 0.31
  lrWellUL = 0.41
  lrEquivocal = 3.75        # equivocal presentation, 3.75 (2.83–5.00)
  lrEquivocalLL = 2.83
  lrEquivocalUL = 5
  lrIll = 14.5              # clinical illness, 14.5 (95% CI 10.2–21.2); 
  lrIllLL = 10.2
  lrIllUL = 21.2
  
  # Risk thresholds (upper limit of 95%CI) for monitoring and management recommendations 
  thresholdBloodCultureVitals = 0.001
  thresholdBloodCultureEmpiricAB = 0.003

  observe({
    
    if(is.numeric(input$ga_weeks) && is.numeric(input$ga_days) && is.numeric(input$highest_temp) && is.numeric(input$rom)) {
      
      intercept <- switch(as.numeric(input$prevalence),
                          38.952265,
                          39.646367,
                          40.05280,
                          40.34150,   
                          40.5656,
                          40.7489,
                          40.903919,  
                          41.0384,
                          41.1571,
                          41.263432,
                          41.965852,
                          42.676976                 # 57.29929499
      )
      
      # Input and calculations
      temp <- input$highest_temp
      temp <- temp * 9 / 5 + 32
      gaWeeks <- input$ga_weeks
      gaDays <- input$ga_days
      ga <- gaWeeks + gaDays/7
      ga2 <- ga * ga
      rom <- input$rom
      rom <- (rom + 0.05)^0.2
      
      if( (input$ip_ab == 2)  ||        # "Broad spectrum antibiotics 2-3.9 hrs before birth" = 2
          (input$ip_ab == 3) ) {        # "GBS specific antibiotics >2 hrs before birth" = 3
        gbsIapORanyABnotOnTime = 1
      }
      else {
        gbsIapORanyABnotOnTime = 0
      }
      
      if(input$ip_ab == 1)             # "Broad spectrum antibiotics >4 hrs before birth" = 1
        broadSpectrumABonTime = 1
      else {
        broadSpectrumABonTime = 0
      }
      
      if(input$gbs_status == 2) {       # "Positive" = 2
        gbsPos = 1
      }
      else {
        gbsPos = 0
      }
      
      if(input$gbs_status == 3) {
        gbsUnknown = 1
      }
      else {
        gbsUnknown = 0
      }
      
      # Logit
      logit = intercept
      logit = logit + temp * betaTemp
      logit = logit + ga * betaGA
      logit = logit + ga2 * betaGA2
      logit = logit + rom * betaROM
      logit = logit + gbsIapORanyABnotOnTime * betaGbsIapORanyABnotOnTime
      logit = logit + broadSpectrumABonTime * betaBroadSpectrumAbOnTime
      logit = logit + gbsPos * betaGbsPos
      logit = logit + gbsUnknown * betaGbsUnknown
      
      # Global risk
      riskAtBirth = 1.0 / (1 + exp(-logit))
      
      # Adjusted risk (95%CI) 
      riskWell        <- riskAtBirth * lrWell / (riskAtBirth * lrWell + (1 - riskAtBirth))
      riskWellLL      <- riskAtBirth * lrWellLL / (riskAtBirth * lrWellLL + (1 - riskAtBirth))
      riskWellUL      <- riskAtBirth * lrWellUL / (riskAtBirth * lrWellUL + (1 - riskAtBirth))
      riskEquivocal   <- riskAtBirth * lrEquivocal / (riskAtBirth * lrEquivocal + (1 - riskAtBirth))
      riskEquivocalLL <- riskAtBirth * lrEquivocalLL / (riskAtBirth * lrEquivocalLL + (1 - riskAtBirth))
      riskEquivocalUL <- riskAtBirth * lrEquivocalUL / (riskAtBirth * lrEquivocalUL + (1 - riskAtBirth))
      riskIll         <- riskAtBirth * lrIll / (riskAtBirth * lrIll + (1 - riskAtBirth))
      riskIllLL       <- riskAtBirth * lrIllLL / (riskAtBirth * lrIllLL + (1 - riskAtBirth))
      riskIllUL       <- riskAtBirth * lrIllUL / (riskAtBirth * lrIllUL + (1 - riskAtBirth))
      
      # Number needed to treat
      nntWell         <- 1 / riskWell;
      nntWellLL       <- 1 / riskWellUL;
      nntWellUL       <- 1 / riskWellLL;
      nntEquivocal    <- 1 / riskEquivocal;
      nntEquivocalLL  <- 1 / riskEquivocalUL;
      nntEquivocalUL  <- 1 / riskEquivocalLL;
      nntIll          <- 1 / riskIll;
      nntIllLL        <- 1 / riskIllUL;
      nntIllUL        <- 1 / riskIllLL;
      
      # OUTPUT
      output$EOS_risk_at_birth <- renderText({
        paste0("Risk of EOS at birth (‰): ", round(1000*riskAtBirth, digits = 2))
      })
      
      if(input$clin_presentation == 1) {
        output$EOS_risk_as_per_presentation <- renderText({
          paste0("If classified as 'well appearing': ", "\n",
                 "Risk of EOS (‰): ",  
                 round(1000*riskWell, digits = 2), " (95%CI ",
                 round(1000*riskWellLL, digits = 2), " - ",
                 round(1000*riskWellUL, digits = 2), ")", "\n",
                 "NNT: ", 
                  round(nntWell, digits = 0), " (95%CI ",
                  round(nntWellLL, digits = 0), " - ",
                  round(nntWellUL, digits = 0), ")")
        })
        if(riskWellUL < thresholdBloodCultureVitals)
        {
          clinical_recommendation <- "No culture, no antibiotics"
          vitals <- "Routine vitals"
          bg_color <- "#22BB33"
          fg_color <- "#372525"
          ICON <- "check-circle"
        }
        
        if(riskWellUL >= thresholdBloodCultureVitals && riskWellUL < thresholdBloodCultureEmpiricAB)
        {
          clinical_recommendation <- "Blood culture"
          vitals <- "Vitals q4 hrs for 24 hrs"
          bg_color <- "#F0AD4E"
          fg_color <- "#373234"
          ICON <- "exclamation-circle"
        }
        
        if(riskWellUL >= thresholdBloodCultureEmpiricAB)
        {
          clinical_recommendation <- "Empiric antibiotics"
          vitals <- "Vitals per NICU"
          bg_color <- "#BB2124"
          fg_color <- "#F7F7F7"
          ICON <- "exclamation-triangle"
        }
          
      }
      else if(input$clin_presentation == 2) {
        output$EOS_risk_as_per_presentation <- renderText({
          paste0("If classified as 'equivocal presentation': ", "\n",
                 "Risk of EOS (‰): ", 
                 round(1000*riskEquivocal, digits = 2), " (95%CI ",
                 round(1000*riskEquivocalLL, digits = 2), " - ",
                 round(1000*riskEquivocalUL, digits = 2), ")", "\n",
                 "NNT: ", 
                  round(nntEquivocal, digits = 0), " (95%CI ",
                  round(nntEquivocalLL, digits = 0), " - ",
                  round(nntEquivocalUL, digits = 0), ")")
        })
        
        if(riskEquivocalUL < thresholdBloodCultureVitals)
        {
          # clGreen;
          clinical_recommendation <- "No culture, no antibiotics"
          vitals <-  "Routine vitals"		
          bg_color <- "#22BB33"
          fg_color <- "#372525"
          ICON <- "check-circle"
        }
        
        if(riskEquivocalUL >= thresholdBloodCultureVitals && riskEquivocalUL < thresholdBloodCultureEmpiricAB)
        {
          clinical_recommendation <- "Blood culture"
          vitals <- "Vitals q4 hrs for 24 hrs"
          bg_color <- "#F0AD4E"
          fg_color <- "#373234"
          ICON <- "exclamation-circle"
        }
        
        if(riskEquivocalUL >= thresholdBloodCultureEmpiricAB)
        {
          clinical_recommendation <- "Empiric antibiotics";
          vitals <- "Vitals per NICU"
          bg_color <- "#BB2124"
          fg_color <- "#F7F7F7"
          ICON <- "exclamation-triangle"
        }
        
      } 
      else if(input$clin_presentation == 3) {
        output$EOS_risk_as_per_presentation <- renderText({
          paste0("If classified as 'clinical illness': ", "\n",
                 "Risk of EOS (‰): ",
                 round(1000*riskIll, digits = 2), " (95%CI ",
                 round(1000*riskIllLL, digits = 2), " - ",
                 round(1000*riskIllUL, digits = 2), ")", "\n",
                 "NNT: ", 
                  round(nntIll, digits = 0), " (95%CI ",
                  round(nntIllLL, digits = 0), " - ",
                  round(nntIllUL, digits = 0), ")")
        })
        
        if(riskIllUL < thresholdBloodCultureEmpiricAB)
        {
          # clRed;
          clinical_recommendation <- "Strongly consider empiric antibiotics"
          vitals <- "Vitals per NICU"
          bg_color <- "#BB2124"
          fg_color <- "#F7F7F7"
          ICON <- "exclamation-triangle"
        }
        
        if(riskIllUL >= thresholdBloodCultureEmpiricAB)
        {
          # clRed;
          clinical_recommendation <- "Empiric antibiotics";
          vitals <- "Vitals per NICU";
          bg_color <- "#BB2124"
          fg_color <- "#F7F7F7"
          ICON <- "exclamation-triangle"
        }
      } 

      output$box_recommendation<-renderUI ({
        value_box(                                
          title = "Clinical recommendation", value = tags$p(clinical_recommendation, style = "font-size: 22px;"),
          theme = value_box_theme(bg = bg_color, fg = fg_color),
          showcase = bsicons::bs_icon(ICON), showcase_layout = "left center",
          full_screen = FALSE, fill = TRUE, height = NULL
        )
      })
      
      output$box_vitals<-renderUI({
        value_box(
          title = "Vitals", value = tags$p(vitals, style = "font-size: 22px;"),
          theme = value_box_theme(bg = bg_color, fg = fg_color),
          showcase = bsicons::bs_icon(ICON), showcase_layout = "left center",
          full_screen = FALSE, fill = TRUE, height = NULL
        )
      })
      
    } # END if
    else {
      output$EOS_risk_at_birth <- renderText({
        "Required input missing"
      })
      
      output$EOS_risk_as_per_presentation <- renderText({
        ""   # Required input missing"        
      })
      
      output$box_recommendation<-renderUI ({
        value_box(
          title = "Clinical recommendation", value = tags$p("No advice due to missing input", style = "font-size: 22px;"),
          theme = value_box_theme(bg = "#F9FAFA", fg = "#400040"),
          showcase = bsicons::bs_icon("info-square"), showcase_layout = "left center",
          full_screen = FALSE, fill = TRUE, height = NULL
        )
      })
      
      output$box_vitals<-renderUI({
        value_box(
          title = "Vitals", value = tags$p("No advice due to missing input", style = "font-size: 22px;"),
          theme = value_box_theme(bg = "#F9FAFA", fg = "#400040"),
          showcase = bsicons::bs_icon("info-square"), showcase_layout = "left center",
          full_screen = FALSE, fill = TRUE, height = NULL
        )
      })
    }
    
  }) # END observe({})
  
  observeEvent( list(input$a1, input$a2, input$a3, input$a4), {
    if(input$a1 | input$a2 |input$a3 | input$a4) {
      updateRadioButtons(session = session, inputId = "btnClinIllness", label = NULL, choices = c("Clinical illness"), inline = TRUE, selected = "Clinical illness")
      updateRadioButtons(session = session, inputId = "btnEquivocal", label = NULL, choices = c("Equivocal"), inline = TRUE, selected = character(0))
      updateRadioButtons(session = session, inputId = "btnWellAppearing", label = NULL, choices = c("Well appearing"), inline = TRUE, selected = character(0))
      updateCheckboxInput(session = session, inputId = "d1", label = NULL, value = FALSE)
    }
    else {
      updateRadioButtons(session = session, inputId = "btnClinIllness", label = NULL, choices = c("Clinical illness"), inline = TRUE, selected = character(0))
      # if(sum(input$b1+input$b2+input$b3+input$b4) >= 1 || sum(input$c1+input$c2+input$c3+input$c4) >= 2) {
      #   updateRadioButtons(session = session, inputId = "btnEquivocal", label = NULL, choices = c("Equivocal"), inline = TRUE, selected = "Equivocal")
      # }
    }
  })
  
  observeEvent( list(input$b1, input$b2, input$b3, input$b4, input$c1, input$c2, input$c3, input$c4), {
    if( (sum(input$b1+input$b2+input$b3+input$b4) >= 1 || sum(input$c1+input$c2+input$c3+input$c4) >= 2) )  {     # && sum(input$a1+input$a2+input$a3+input$a4) == 0)
      updateRadioButtons(session = session, inputId = "btnEquivocal", label = NULL, choices = c("Equivocal"), inline = TRUE, selected = "Equivocal")
      updateRadioButtons(session = session, inputId = "btnWellAppearing", label = NULL, choices = c("Well appearing"), inline = TRUE, selected = character(0))
      
      updateCheckboxInput(session = session, inputId = "d1", label = NULL, value = FALSE)  # not necessary
    }
    else {
      updateRadioButtons(session = session, inputId = "btnEquivocal", label = NULL, choices = c("Equivocal"), inline = TRUE, selected = character(0))      
    }
  })
  
  observeEvent(input$d1, {
    if(input$d1) {
      updateRadioButtons(session = session, inputId = "btnWellAppearing", label = NULL, choices = c("Well appearing"), inline = TRUE, selected = "Well appearing")
      updateRadioButtons(session = session, inputId = "btnClinIllness", label = NULL, choices = c("Clinical Illness"), inline = TRUE, selected = character(0))
      updateRadioButtons(session = session, inputId = "btnEquivocal", label = NULL, choices = c("Equivocal"), inline = TRUE, selected = character(0))
    }
    else {
      updateRadioButtons(session = session, inputId = "btnWellAppearing", label = NULL, choices = c("Well appearing"), inline = TRUE, selected = character(0))
    }
  })    
  
  observeEvent(input$btnClinIllness , {
    if(!is.null( input$btnClinIllness ) ) {
      updateRadioButtons(session = session, inputId = "btnClinIllness"  , label = NULL, choices = c("Clinical illness"), inline = TRUE, selected = "Clinical illness")
      updateRadioButtons(session = session, inputId = "btnEquivocal"    , label = NULL, choices = c("Equivocal"),        inline = TRUE, selected = character(0))
      updateRadioButtons(session = session, inputId = "btnWellAppearing", label = NULL, choices = c("Well appearing"),   inline = TRUE, selected = character(0))
      
      updateCheckboxInput(session = session, inputId = "b1", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "b2", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "b3", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "b4", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "c1", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "c2", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "c3", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "c4", label = NULL, value = FALSE)
      
      updateCheckboxInput(session = session, inputId = "d1", label = NULL, value = FALSE)
    }
  })
  
  observeEvent( input$btnEquivocal , {
    if(!is.null( input$btnEquivocal) ) {
      updateRadioButtons(session = session, inputId = "btnClinIllness", label = NULL, choices = c("Clinical illness"), inline = TRUE, selected = character(0))
      updateRadioButtons(session = session, inputId = "btnEquivocal", label = NULL, choices = c("Equivocal"), inline = TRUE, selected = "Equivocal")
      updateRadioButtons(session = session, inputId = "btnWellAppearing", label = NULL, choices = c("Well appearing"), inline = TRUE, selected = character(0))
      
      updateCheckboxInput(session = session, inputId = "a1", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "a2", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "a3", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "a4", label = NULL, value = FALSE)
      
      updateCheckboxInput(session = session, inputId = "d1", label = NULL, value = FALSE)
    }
  })
  
  observeEvent( input$btnWellAppearing, {
    if(!is.null( input$btnWellAppearing) ) {
      updateRadioButtons(session = session, inputId = "btnClinIllness", label = NULL, choices = c("Clinical illness"), inline = TRUE, selected = character(0))
      updateRadioButtons(session = session, inputId = "btnEquivocal", label = NULL, choices = c("Equivocal"), inline = TRUE, selected = character(0))
      updateRadioButtons(session = session, inputId = "btnWellAppearing", label = NULL, choices = c("Well appearing"), inline = TRUE, selected = "Well appearing")
      
      updateCheckboxInput(session = session, inputId = "a1", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "a2", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "a3", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "a4", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "b1", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "b2", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "b3", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "b4", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "c1", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "c2", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "c3", label = NULL, value = FALSE)
      updateCheckboxInput(session = session, inputId = "c4", label = NULL, value = FALSE)
    }
  })

} # END server
  
  
  # https://bslib.shinyapps.io/build-a-box/
  # https://www.rdocumentation.org/packages/bslib/versions/0.8.0/topics/value_box
  
  # https://rstudio.github.io/bslib/articles/dashboards/index.html

  # https://forum.posit.co/t/change-valueboxes-size/71332/2
  # Valid colors are: red, yellow, aqua, blue, light-blue, green, navy, teal, olive, lime, orange, fuchsia, purple, maroon, black.  

